mapper.update:
UPDATE t_train_module
SET code = #{code}, name = #{name}
WHERE id = #{id}

UPDATE t_course
SET code     = #{code}, name = #{name}, school_score = #{schoolScore}, school_hours = #{schoolHours},
    semester = #{semester}, nature = #{nature}, method = #{method}, module_id = #{moduleId}
WHERE id = #{id}

mapper.select:
SELECT t_course.id, code, t_course.name, school_hours schoolHours, school_score schoolScore, semester, method, module.name moduleName, module_id moduleId FROM t_course LEFT JOIN (SELECT id, name FROM t_train_module) module ON module.id = t_course.module_id
and module_id = #{moduleId}
and semester = #{semester}
AND t_course.name LIKE #{name} LIMIT #{start}, #{end}

mapper.update:
UPDATE t_classes
SET level = #{level}, name = #{name}, department_id = #{departmentId}
WHERE id = #{id}

mapper.delete:
UPDATE t_classes
SET teacher_id = ""
WHERE teacher_id = #{no}
